mapper.select:
select user_no , user_name , user_id , user_pwd , birth , post , address , address_detail , hire_date , end_date , email , phone , call , decode (status, 'Y ', '재직' , 'R ', '휴직') status , dept_code , dept_name , decode (job_code, 1, '사원' , 2, '주임' , 3, '대리' , 4, '과장' , 9, '대표이사') job_name , image , admin_yn , nvl(job_code, 0) job_code from employee join department using (dept_code) where (status = 'Y' or status = 'R')
and dept_code = #{ dno }
order by job_code desc , user_name asc

mapper.insert:

insert
  into employee
  (  user_no 
  ,  user_name 
  ,  user_id 
  ,  user_pwd 
  ,  email 
  ,  phone 
  ,  call 
  ,  hire_date 
  ,  status 
  ,  dept_code 
  ,  job_code 
  ,  image
  )
  values
  ( #{ userNo }
  , #{ userName }
  , #{ userId }
  , #{ userPwd }
  , #{ email }
  , #{ phone }
  , #{ call }
  , #{ hire }
  , #{ status }
  , #{ deptCode }
  , #{ jobCode }
  , #{ image }
  )

mapper.update:

update employee
   set 
     user_name = #{ userName }
   , user_pwd = #{ userPwd }
   , hire_date = #{ hire }
   , dept_code = #{ deptCode }
   , job_code = #{ jobCode }
   , email = #{ email }
   , call = #{ call }
   , phone = #{ phone }
   , status = #{ status }
 where user_no = #{ userNo }
