mapper.insert:
INSERT 
  INTO LEAVE_ALIST
  (
    LEAVE_ANO
  , USER_NO
  , APPLY_DATE
  , LEAVE_TYPE
  , START_DATE
  , END_DATE
  , LEAVE_USE
  , REASON
  , STATUS
  ) 
  VALUES
  (
    SEQ_LANO.NEXTVAL
  , #{userNo}
  , SYSDATE
  , #{leaveType}
  , #{startDate}
  , #{endDate}
  , #{leaveUse}
  , #{reason}
  , '대기'
  )

