mapper.update:
UPDATE TNA
   SET CLOCK_OUT = TO_CHAR(SYSDATE, 'HH24:MI')
 WHERE USER_NO = #{userNo}

mapper.insert:
INSERT 
  INTO LEAVE_ALIST
  (
    LEAVE_ANO
  , USER_NO
  , APPLY_DATE
  , LEAVE_TYPE
  , START_DATE
  , END_DATE
  , LEAVE_USE
  , REASON
  , STATUS
  ) 
  VALUES
  (
    SEQ_LANO.NEXTVAL
  , #{userNo}
  , SYSDATE
  , #{leaveType}
  , #{startDate}
  , #{endDate}
  , #{leaveUse}
  , #{reason}
  , '대기'
  )
